import React, { useState } from "react";
import BreakCheck from "./BreakCheck.js";
import classNames from "../utils/class-names";

function ActiveState({ timerData, setTimerData, formattedDurations }) {
  //If timer is not active/in session, do not show additional HTML
  if (!timerData.inSession) return null;

  function determineValue() {
    if ((timerData.timeRemaining%15) === 0) {
        setTimerData({
            ...timerData,
            elapsedValue: timerData.elapsedValue + 1
        });
    }
  }

  return (
    <>
      <div>
        <div className="row mb-2">
          <div className="col">
            <h2 data-testid="session-title">
              <BreakCheck
                timerData={timerData}
                formattedDurations={formattedDurations}
              />
            </h2>
            <p className="lead" data-testid="session-sub-title">
              {formattedDurations.timer} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={determineValue}
                style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ActiveState;
