import React, { useState } from "react";
import classNames from "../utils/class-names";

function ActiveState({
  initialTimerData,
  timerData,
  setTimerData,
  formattedDurations,
}) {
  if (!timerData.inSession) return null;

  if (timerData.inSession && !timerData.isTimerRunning) {
    const showPaused = true
  } else {
    const showPaused = false
  }

  return (
    <>
      <div>
        <div className="row mb-2">
          <div className="col">
            <h2 data-testid="session-title">Focusing for {formattedDurations.focusMinutes} minutes</h2>
            <p className="lead" data-testid="session-sub-title">
              {formattedDurations.focusSeconds} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
              <h2 disabled={showPaused}>PAUSED</h2>
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ActiveState;
