import React from "react";
import useInterval from "../utils/useInterval";
import BreakCheck from "./BreakCheck.js";

function ActiveState({ timerData, setTimerData, formattedDurations }) {
  //If timer is not active/in session, do not show additional HTML
  if (!timerData.inSession) return null;

  const startingTime = timerData.isBreak ? timerData.breakDuration*60 : timerData.focusDuration*60;

  if (timerData.timeRemaining < startingTime) {
      const percentage = Math.round(timerData.timeRemaining / startingTime);

      setTimerData({
        ...timerData,
        elapsedValue: percentage,
      });
  }

  return (
    <>
      <div>
        <div className="row mb-2">
          <div className="col">
            <h2 data-testid="session-title">
              <BreakCheck
                timerData={timerData}
                formattedDurations={formattedDurations}
              />
            </h2>
            <p className="lead" data-testid="session-sub-title">
              {formattedDurations.timer} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={timerData.elapsedValue}
                style={{ width: `${timerData.elapsedValue}%` }}
              />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ActiveState;
