import React, { useState } from "react";
import classNames from "../utils/class-names";

function ActiveState({
  initialTimerData,
  timerData,
  setTimerData,
  formattedDurations,
}) {
  //If timer is not active/in session, do not show additional HTML
  if (!timerData.inSession) return null;

  //Check if timer is focusing or on break
  const checkBreak = () => {
    if (timerData.isBreak) {
      return formattedDurations.breakMinutes;
    } else {
      return formattedDurations.focusMinutes;
    }
  };

  return (
    <>
      <div>
        <div className="row mb-2">
          <div className="col">
            <h2 data-testid="session-title">
              Focusing for {formattedDurations.focusMinutes} minutes
            </h2>
            <p className="lead" data-testid="session-sub-title">
              {formattedDurations.timer} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ActiveState;
