import React, { useState } from "react";
import classNames from "../utils/class-names";

function InitialState(initialTimerData, timerData, setTimerData) {
  function handleDurationClick({ target }) {
    if (5 < timerData.focusDuration && timerData.focusDuration < 60) {
      if (target.value === "decreaseFocus") {
        setTimerData({
          ...timerData,
          focusDuration: timerData.focusDuration - 1,
        });
      } else if (target.value === "increaseFocus") {
        setTimerData({
          ...timerData,
          focusDuration: timerData.focusDuration + 1,
        });
      }
    }

    if (1 < timerData.breakDuration && timerData.breakDuration < 15) {
      if (target.value === "decreaseBreak") {
        setTimerData({
          ...timerData,
          breakDuration: timerData.breakDuration - 1,
        });
      } else if (target.value === "increaseBreak") {
        setTimerData({
          ...timerData,
          breakDuration: timerData.breakDuration + 1,
        });
      }
    }
  }

  return (
    <>
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              Focus Duration: {timerData.focusDuration}
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                value="decreaseFocus"
                disabled={timerData.inSession}
                onClick={handleDurationClick}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                value="increaseFocus"
                disabled={timerData.inSession}
                onClick={handleDurationClick}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {timerData.breakDuration}
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  value="decreaseBreak"
                  disabled={timerData.inSession}
                  onClick={handleDurationClick}
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  value="increaseBreak"
                  disabled={timerData.inSession}
                  onClick={handleDurationClick}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !timerData.isTimerRunning,
                  "oi-media-pause": timerData.isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              disabled={!timerData.inSession}
              onClick={stopSession}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default InitialState;
